import React, { useState } from 'react';

const Layout = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const sideMenuStyle = {
        height: '100%',
        width: isOpen ? '250px' : '0',
        position: 'fixed',
        zIndex: 10,
        top: 0,
        left: 0,
        backgroundColor: '#343a40',
        overflowX: 'hidden',
        transition: '0.5s',
        paddingTop: '60px'
    };

    const navbarStyle = {
        position: 'relative',
        zIndex: 20
    };

    const toggleButtonStyle = {
        position: 'relative',
        zIndex: 30
    };

    const menuItemStyle = {
        padding: '8px 8px 8px 32px',
        textDecoration: 'none',
        fontSize: '18px',
        color: '#818181',
        display: 'block',
        transition: '0.3s'
    };

    const closeButtonStyle = {
        position: 'absolute',
        top: '15px',
        right: '25px',
        fontSize: '36px',
        color: '#818181',
        cursor: 'pointer'
    };

    return (
        <div>
            <div style={sideMenuStyle}>
                <span style={closeButtonStyle} onClick={toggleMenu}>&times;</span>
                <div style={menuItemStyle}>Home</div>
                <div style={menuItemStyle}>About</div>
                <div style={menuItemStyle}>Services</div>
                <div style={menuItemStyle}>Contact</div>
            </div>
            <nav className="navbar navbar-dark bg-dark" style={navbarStyle}>
                <button 
                    className="navbar-toggler" 
                    type="button" 
                    onClick={toggleMenu}
                    style={toggleButtonStyle}
                >
                    <span className="navbar-toggler-icon"></span>
                </button>
                <span className="navbar-brand mb-0 h1" style={{ marginLeft: '-15px' }}>Story Writer</span>
            </nav>
            <main>
                {children}
            </main>
        </div>
    );
};

export default Layout;
