import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Layout from '../components/Layout';

const Home = () => {
    const [formData, setFormData] = useState({
        language: '',
        storyType: '',
        storyLength: '',
        model: '',
        settings: {
            temperature: 0.7,
            maxTokens: 1000,
            topP: 1
        }
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [story, setStory] = useState(null);

    const [apiKeys, setApiKeys] = useState({
        geminiApiKey: '',
        gptApiKey: '',
        deepseekApiKey: ''
    });

    useEffect(() => {
        // Load settings from localStorage
        const savedSettings = JSON.parse(localStorage.getItem('settings') || '{}');
        setApiKeys({
            geminiApiKey: savedSettings.geminiApiKey || '',
            gptApiKey: savedSettings.gptApiKey || '',
            deepseekApiKey: savedSettings.deepseekApiKey || ''
        });
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSettingsChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevData => ({
            ...prevData,
            settings: {
                ...prevData.settings,
                [name]: parseFloat(value)
            }
        }));
    };

    const handleGenerate = async () => {
        try {
            setLoading(true);
            setError(null);

            // Get API key based on selected model
            let apiKey = '';
            if (formData.model === 'gpt3.5' || formData.model === 'gpt4') {
                apiKey = apiKeys.gptApiKey;
            } else if (formData.model === 'claude') {
                apiKey = apiKeys.deepseekApiKey;
            } else {
                apiKey = apiKeys.geminiApiKey;
            }

            const response = await axios.post('http://localhost:5000/api/generate', {
                ...formData,
                apiKey
            });
            
            setStory(response.data.story);
        } catch (err) {
            setError(err.message || 'Failed to generate story');
        } finally {
            setLoading(false);
        }
    };

    return (
        <Layout>
            <div className="container">
                <h1 className="text-center mt-4">Welcome to Story Generater</h1>
                <p className="text-center">Generate your amazing stories now!</p>
                
                <div className="d-flex align-items-center" style={{ minHeight: 'calc(100vh - 200px)' }}>
                    <div className="w-100">
                        <div className="row">
                            <div className="col-md-3">
                                <input 
                                    list="languages" 
                                    className="form-control" 
                                    placeholder="Select or Enter Language"
                                    name="language"
                                    value={formData.language}
                                    onChange={handleInputChange}
                                />
                                <datalist id="languages">
                                    <option value="Hindi" />
                                    <option value="English" />
                                </datalist>
                            </div>
                            
                            <div className="col-md-3">
                                <input 
                                    list="storyTypes" 
                                    className="form-control" 
                                    placeholder="Select or Enter Story Type"
                                    name="storyType"
                                    value={formData.storyType}
                                    onChange={handleInputChange}
                                />
                                <datalist id="storyTypes">
                                    <option value="Horror" />
                                    <option value="Time Travel" />
                                    <option value="Mystery" />
                                    <option value="Adventure" />
                                    <option value="Science Fiction" />
                                </datalist>
                            </div>
                            
                            <div className="col-md-3">
                                <select 
                                    className="form-select" 
                                    name="storyLength"
                                    value={formData.storyLength}
                                    onChange={handleInputChange}
                                >
                                    <option value="">Select Story Length</option>
                                    <option value="short">Short (~ 20min to 40min)</option>
                                    <option value="medium">Medium (~ 50min to 80min)</option>
                                    <option value="long">Long (~ 90min to 120min)</option>
                                    <option value="extra_long">Extra Long (~ 130min to 180min)</option>
                                </select>
                            </div>
                            
                            <div className="col-md-3">
                                <select 
                                    className="form-select" 
                                    name="model"
                                    value={formData.model}
                                    onChange={handleInputChange}
                                >
                                    <option value="">Select Model</option>
                                    <option value="gpt3.5">GPT-3.5</option>
                                    <option value="gpt4">GPT-4</option>
                                    <option value="claude">Claude</option>
                                    <option value="llama">LLaMA</option>
                                </select>
                            </div>
                        </div>
                        {/* <div className="row mt-3">
                            <div className="col-md-4">
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    placeholder="Temperature (0-1)"
                                    name="temperature"
                                    value={formData.settings.temperature}
                                    onChange={handleSettingsChange}
                                    step="0.1"
                                    min="0"
                                    max="1"
                                />
                            </div>
                            <div className="col-md-4">
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    placeholder="Max Tokens"
                                    name="maxTokens"
                                    value={formData.settings.maxTokens}
                                    onChange={handleSettingsChange}
                                    min="1"
                                />
                            </div>
                            <div className="col-md-4">
                                <input 
                                    type="number" 
                                    className="form-control" 
                                    placeholder="Top P (0-1)"
                                    name="topP"
                                    value={formData.settings.topP}
                                    onChange={handleSettingsChange}
                                    step="0.1"
                                    min="0"
                                    max="1"
                                />
                            </div>
                        </div> */}
                        <div className="row mt-3">
                            <div className="col-12">
                                <button 
                                    className="btn btn-primary w-100"
                                    onClick={handleGenerate}
                                    disabled={loading}
                                >
                                    {loading ? 'Generating...' : 'Generate'}
                                </button>
                                {error && (
                                    <div className="alert alert-danger mt-3">
                                        {error}
                                    </div>
                                )}
                                {story && (
                                    <div className="mt-4">
                                        <h3>Generated Story:</h3>
                                        <div className="card">
                                            <div className="card-body">
                                                {story}
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </Layout>
    );
};

export default Home;
