import React, { useState } from 'react';

const Layout = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const containerStyle = {
        position: 'relative',
        minHeight: '100vh'
    };

    const overlayStyle = {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: 'rgba(0,0,0,0.5)',
        display: isOpen ? 'block' : 'none',
        zIndex: 15
    };

    const sideMenuStyle = {
        height: '100%',
        width: '250px',
        position: 'fixed',
        zIndex: 20,
        top: 0,
        left: isOpen ? 0 : '-250px',
        backgroundColor: '#343a40',
        overflowX: 'hidden',
        transition: '0.3s',
        paddingTop: '60px'
    };

    const menuItemStyle = {
        padding: '8px 8px 8px 32px',
        textDecoration: 'none',
        fontSize: '18px',
        color: '#818181',
        display: 'block',
        transition: '0.3s'
    };

    const closeButtonStyle = {
        position: 'absolute',
        top: '15px',
        right: '25px',
        fontSize: '36px',
        color: '#818181',
        cursor: 'pointer'
    };

    return (
        <div style={containerStyle}>
            {/* Overlay */}
            <div style={overlayStyle} onClick={toggleMenu}></div>

            {/* Side Menu */}
            <div style={sideMenuStyle}>
                <span style={closeButtonStyle} onClick={toggleMenu}>&times;</span>
                <div style={menuItemStyle}>Home</div>
                <div style={menuItemStyle}>About</div>
                <div style={menuItemStyle}>Services</div>
                <div style={menuItemStyle}>Contact</div>
            </div>

            {/* Main Content */}
            <div style={{ position: 'relative', zIndex: 10 }}>
                <nav className="navbar navbar-dark bg-dark">
                    <button 
                        className="navbar-toggler" 
                        type="button" 
                        onClick={toggleMenu}
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <span className="navbar-brand mb-0 h1" style={{ marginLeft: '-15px' }}>Story Writer</span>
                </nav>
                <main>
                    {children}
                </main>
            </div>
        </div>
    );
};

export default Layout;
