import React, { useState } from 'react';

const Layout = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    const sideMenuStyle = {
        height: '100vh',
        width: '250px',
        position: 'fixed',
        top: 0,
        left: isOpen ? 0 : '-250px',
        backgroundColor: '#343a40',
        transition: '0.3s ease-in-out',
        paddingTop: '60px',
        zIndex: 1000
    };

    const navbarContainerStyle = {
        position: 'fixed',
        top: 0,
        left: 0,
        right: 0,
        zIndex: 1001
    };

    const mainContentStyle = {
        paddingTop: '56px'  // Height of navbar
    };

    return (
        <>
            <div style={navbarContainerStyle}>
                <nav className="navbar navbar-dark bg-dark">
                    <button 
                        className="navbar-toggler" 
                        type="button" 
                        onClick={toggleMenu}
                    >
                        <span className="navbar-toggler-icon"></span>
                    </button>
                    <span className="navbar-brand mb-0 h1" style={{ marginLeft: '-15px' }}>Story Writer</span>
                </nav>
            </div>

            <div style={sideMenuStyle}>
                <span 
                    style={{
                        position: 'absolute',
                        top: '15px',
                        right: '25px',
                        fontSize: '36px',
                        color: '#818181',
                        cursor: 'pointer'
                    }} 
                    onClick={toggleMenu}
                >
                    &times;
                </span>
                <div style={{ padding: '8px 8px 8px 32px', color: '#818181' }}>Home</div>
                <div style={{ padding: '8px 8px 8px 32px', color: '#818181' }}>About</div>
                <div style={{ padding: '8px 8px 8px 32px', color: '#818181' }}>Services</div>
                <div style={{ padding: '8px 8px 8px 32px', color: '#818181' }}>Contact</div>
            </div>

            <div style={mainContentStyle}>
                {children}
            </div>
        </>
    );
};

export default Layout;
